// Guarda el archivo Excel en la variable de contexto 
flow.set("archivoExcel", msg.payload); 
msg.payload = " ";
return null;

var dataArray = msg.payload; // Ej: [ ["Encabezado1", "Encabezado2"], ["Dato1", "Dato2"], ... ] 
var headers = dataArray[0]; 
var dataObjects = []; 
// Recorrer el array empezando desde la segunda fila 
for (var i = 1; i < dataArray.length; i++) {     
          var row = dataArray[i];     
          var obj = {};     
          for (var j = 0; j < headers.length; j++){         
            obj[headers[j]] = row[j]; 
          }     
          dataObjects.push(obj); 
} 
msg.payload = dataObjects; 
return msg;

// Recupera el archivo Excel almacenado en la variable de contexto 
var archivo = flow.get("archivoExcel"); 
flow.set("archivoExcel", null);  
// Verificar si el archivo existe 
if (!archivo) { 
    // Si no se encontró, se devuelve un error     
    msg.statusCode = 404;     
    msg.payload = "El archivo Excel no está disponible o no se ha generado.";     
    return msg; 
}  
// Establecer las cabeceras HTTP para que el navegador lo trate como un archivo descargable en formato Excel 
msg.headers = { 
    "Content-Type": "application/vnd.openxmlformatsofficedocument.spreadsheetml.sheet", 
    "Content-Disposition": 'attachment; filename="reporte.xlsx"' 
};  
// Colocar en msg.payload el contenido binario del archivo Excel 
msg.payload = archivo; 
return msg;

// guardar el valor en contexto de flujo
flow.set('datoTemp', msg.topic);
msg.payload = msg.topic;
return msg;

// guardar el valor en contexto de flujo
flow.set('datoluz', msg.payload);
return msg;

// guardar el valor en contexto de flujo
flow.set('datohum_suelo', msg.payload);
return msg;

// guardar el valor en contexto de flujo
flow.set('datohum_ambiente', msg.payload);
return msg;

// Obtener últimos valores
const fecha = new Date().toLocaleDateString('en-CA', { timeZone: 'America/La_Paz'}); // YYYY-MM-DD
const t  = flow.get('temp')        || 0;
const l  = flow.get('luz')         || 0;
const hs = flow.get('hum_suelo')   || 0;
const ha = flow.get('hum_ambiente')|| 0;
// Montar el array para Google Sheets
msg.payload = [ fecha, t, l, hs, ha ];
return msg;

// msg.req.query.token viene de GET /validador?token=...
var token = msg.req.query.token;

// 1) Si falta el token: 401
if (!token) {
    msg.statusCode = 401;
    msg.payload    = "Token faltante";
    return msg;
}

// 2) Decodifica Base64 a UTF-8: "usuario|timestamp|firma"
try {
    msg.decoded = Buffer.from(token, "base64").toString("utf8");
} catch (e) {
    msg.statusCode = 400;
    msg.payload    = "Token inválido (Base64)";
    return msg;
}

// 3) Separa en 3: usuario | timestamp | firma
var parts = msg.decoded.split("|");
if (parts.length !== 3) {
    msg.statusCode = 400;
    msg.payload    = "Formato de token incorrecto";
    return msg;
}

msg.username  = parts[0];   // p.ej. 'juan'
msg.ts_string = parts[1];   // p.ej. '1623000000'
//msg.sig       = parts[2];   // hex HMAC-SHA256
flow.set("sig", parts[2]); 
return msg;

// Reconstruye el payload que firmó el PHP:
msg.payload = msg.username+"|"+msg.ts_string;

// Define tu secreto compartido (16–24 carácteres):
// Línea en tu Function que define el secreto:
// Debe coincidir carácter por carácter con el string de PHP,
// pero en JS las barras invertidas se escapan con otra barra invertida.
//msg.secret = "7uhI2B1mQJf)KoH(3q3AmInMvn9z;+1=";

// Dejamos msg.sig para comparar luego:
// msg.sig = firma que vino en el token
return msg;

// 1) Compara la firma calculada con la del token
var sign = flow.get("sig"); 
if (sign !== msg.payload) {
    msg.statusCode = 401;
    msg.payload = "Firma inválida";
    return msg;
}

// 2) Comprueba expiración (1 hora = 3600 s)
var ts = parseInt(msg.ts_string, 10);
var now = Math.floor(Date.now() / 1000);
if (now - ts > 3600) {
    msg.statusCode = 401;
    msg.payload = "Token expirado";
    return msg;
}

// 3) Si todo OK, preparamos la cookie y redirect:
msg.headers = {
  // Quitamos HttpOnly para que el script en <head> pueda leerla
  "Set-Cookie": "auth=1; Path=/; SameSite=Lax"
};
// Redirige al dashboard completo (incl. #! y socketid)
msg.statusCode = 302;
//msg.headers["Location"] = "https://node-red-bh0b.onrender.com/ui/#!/0?socketid=NKUKX8V5cUyfuiTHAAAB";
msg.headers["Location"] = "https://node-red-bh0b.onrender.com/ui1";
msg.payload = "";
return msg;

// 1) Preparamos la cookie para borrarla: 
//    - Asignamos valor vacío y expiración pasada.
//    - Path=/ para que afecte a toda la ruta.
msg.headers = {
  // Igual, sin HttpOnly
  "Set-Cookie": "auth=; Path=/; Expires=Thu, 01 Jan 1970 00:00:00 GMT; SameSite=Lax"
};

// 2) Redirigimos al usuario de vuelta al login PHP en InfinityFree
msg.statusCode = 302;
msg.headers["Location"] = "https://datagauge.kesug.com/cerrar_sesion.php";

// 3) Vacíamos el cuerpo de la respuesta (solo interesan cabeceras)
msg.payload = "";
return msg;

// 1. Hora actual 
const now = Math.floor(Date.now()/1000);

// 2. Claim estándar para Google OAuth2 JWT
// OPCION B, BLOQUE DE CODIGO (claim) PARA DESCARGAR 
// EL ARCHIVO EXCEL DE GOOGLE SHEETS
/*const claim = {
  iss: "node-red-sheets@ninth-optics-458601-r5.iam.gserviceaccount.com", // client_email
  scope: "https://www.googleapis.com/auth/spreadsheets",     // lectura y escritura
  aud: "https://oauth2.googleapis.com/token",                          // endpoint token
  iat: now,                                                            // issued at                                                             // expiration
};*/
const claim = {
  iss: "node-red-sheets@ninth-optics-458601-r5.iam.gserviceaccount.com",
  // Tu client_email de la cuenta de servicio
  scope: [
    "https://www.googleapis.com/auth/spreadsheets",      // Sheets API: read & write
    "https://www.googleapis.com/auth/drive.readonly"     // Drive API: export .xlsx
  ].join(" "),                                           // Ambos scopes separados por espacio
  aud: "https://oauth2.googleapis.com/token",            // Endpoint de token de Google
  iat: now,                                              // “Issued At”: cuándo se generó                                               // “Expiration”: hasta cuándo es válido
};
msg.payload = claim;       // lo mandamos al nodo JWT sign
return msg;

// Function: “Build Token Request Body”
msg.payload =
  "grant_type=urn:ietf:params:oauth:grant-type:jwt-bearer" +
  "&assertion=" + encodeURIComponent(msg.token);
// Como estamos en POST, msg.payload es el cuerpo
return msg;

// 1. Obtener el JSON con el access_token del nodo anterior
const tokenResponse = msg.payload;  
//    tokenResponse debe tener { access_token, expires_in, token_type }
const accessToken = tokenResponse.access_token;  // extraemos sólo el token

// 2. Preparar los headers para la llamada a Drive
msg.headers = {
  Authorization: `Bearer ${accessToken}`         // formato: "Bearer <tu_token>"
};

// 3. Definir el ID de tu Spreadsheet
const spreadsheetId = "1CGfI2dkxgYdfw37UlectzlLVmVtBW0EQ8MUh9ni5R6Q";
// OPCION B BLOQUE DE CODIGO PARA DESCARGAR EL 
// ARCHIVO EXCEL DE GOOGLE SHEETS
// const range = "hoja1!A1:Z2000";  
// msg.url =
// `https://sheets.googleapis.com/v4/spreadsheets/` +  // endpoint base
// `${spreadsheetId}/values/${encodeURIComponent(range)}`;  

// 4. Montar la URL de exportación de Drive
//    Este endpoint exporta el archivo .xlsx completo, con todos los estilos intactos.
msg.url =
  `https://www.googleapis.com/drive/v3/files/` +
  `${spreadsheetId}/export` +
  `?mimeType=` +
  `application/vnd.openxmlformats-officedocument.spreadsheetml.sheet`;

// 5. Devolver el mensaje para que el nodo HTTP Request descargue el buffer binario
return msg;

// OPCION B PARA DESCARGAR EL ARCHIVO EXCEL DE
// GOOGLE SHEETS
// SI OPTAMOS POR ESTA OPCION DEBEMOS CONECTAR ESTE 
// NODO (function 13) DESPUES DEL NODO http request (solicitud)
// Y CONECTAR EL NODO xlsx - out ANTES DEL NODO function (function 14)
// ES DECIR A SU ENTRADA
// 1) Extraemos de msg.payload la matriz de valores (array de arrays)
//    que devolvió la API de Google Sheets. Si no existe, usamos [].
const allValues = msg.payload.values || [];

// 2) Si no hay al menos 2 filas (una de encabezados y otra de datos),  
//    devolvemos un array vacío y paramos aquí.
if (allValues.length < 2) {
    msg.payload = [];
    return msg;
}

// 3) La fila 0 de allValues es el array de encabezados (nombres de columnas)
const headers = allValues[0];

// 4) A partir de la fila 1 empiezan los datos reales
const rows = allValues.slice(1);

// 5) Convertimos cada fila en un objeto:
//    - Map devuelve un nuevo array con el mismo número de filas.
//    - Por cada fila, creamos un objeto vacío.
//    - Recorremos cada encabezado (headers) para asignar:
//        obj[ headers[i] ] = row[i] (o '' si no existe valor).
const objects = rows.map(row => {
    const obj = {};                          // Objeto para esta fila
    headers.forEach((colName, idx) => {
        // Cada columna recibe su valor; si falta, ponemos cadena vacía
        obj[colName] = row[idx] !== undefined ? row[idx] : '';
    });
    return obj;                              // Devolvemos el objeto construido
});

// 6) Finalmente, msg.payload debe ser el array de objetos
msg.payload = objects;

// 7) Y retornamos el mensaje para que lo procese XLSX-Out
return msg;

// 1. Guardamos el Buffer (.xlsx) en la variable de contexto "archivoExcel"
flow.set("archivoExcel", msg.payload);  

// 2. Limpiamos msg.payload para no propagar datos binarios
msg.payload = "";                       

// 3. Retornamos null: no enviamos mensaje hacia abajo
return null;

// 1. Hora actual 
const now = Math.floor(Date.now()/1000);

// 2. Claim estándar para Google OAuth2 JWT
const claim = {
  iss: "node-red-sheets@ninth-optics-458601-r5.iam.gserviceaccount.com", // client_email
  scope: "https://www.googleapis.com/auth/spreadsheets",     // lectura y escritura
  aud: "https://oauth2.googleapis.com/token",                          // endpoint token
  iat: now,                                                            // issued at                                                             // expiration
};

msg.payload = claim;       // lo mandamos al nodo JWT sign
return msg;

// Function: “Build Token Request Body”
msg.payload =
  "grant_type=urn:ietf:params:oauth:grant-type:jwt-bearer" +
  "&assertion=" + encodeURIComponent(msg.token);
// Como estamos en POST, msg.payload es el cuerpo
return msg;

// --- Contador dinámico con reinicio por cambio de initial o de límite ---

// 1) Valores configurables manualmente
const newInitial = 5;     // ← valor de partida fila desde donde empieza escribir
const maxValue   = 10;  // ← tope donde reinicia
var startRow;

// 1.1) Recuperar estados previos
const lastInitial    = flow.get('last_initial');
const lastMax        = flow.get('last_max');
const currentCounter = flow.get('counter');

// 1.2) Determinar si hay que reiniciar por primera vez,
//    o porque cambió newInitial, o porque cambió maxValue
const initialChanged = (lastInitial === undefined) || (newInitial !== lastInitial);
const maxChanged     = (lastMax === undefined) || (maxValue   !== lastMax);

if (initialChanged || maxChanged) {
    // Reinicio total
    flow.set('counter', newInitial);
    flow.set('last_initial', newInitial);
    flow.set('last_max', maxValue);
    startRow = newInitial;
} else {
    // Incremento normal
    let nextValue = currentCounter + 1;

    // Cuando se alcanza el tope actual, reiniciar a newInitial
    if (nextValue > maxValue) {
        nextValue = newInitial;
    }

    // Guardar y preparar salida
    flow.set('counter', nextValue);
    startRow = nextValue;
}

// 2) OBTENER LOS DATOS A ESCRIBIR DESDE flow.context
const fecha = new Date().toLocaleDateString('en-CA', { timeZone: 'America/La_Paz' }); // YYYY-MM-DD       || '';  // Fecha o cadena vacía si no existe
const t = flow.get('temp')          || '';  // Temperatura
const l = flow.get('luz')           || '';  // Intensidad de luz
const hs = flow.get('hum_suelo')    || '';  // Humedad de suelo
const ha = flow.get('hum_ambiente') || '';  // Humedad ambiente
// Si tienes más datos, continúa así:
// const dato6 = flow.get('otroDato') || '';

// 3) MONTAR EL ARRAY newValues CON TODOS LOS DATOS
const newValues = [fecha, t, l, hs, ha];

// 4) DATOS FIJOS DE CONFIGURACIÓN
const spreadsheetId = "1CGfI2dkxgYdfw37UlectzlLVmVtBW0EQ8MUh9ni5R6Q";
const sheetName = "hoja1";

// 5) FUNCIÓN AUXILIAR: CONVERTIR ÍNDICE DE COLUMNA A LETRA EXCEL
function columnLetter(colIndex) {
    let letter = "";
    while (colIndex > 0) {
        const mod = (colIndex - 1) % 26;
        letter = String.fromCharCode(65 + mod) + letter;
        colIndex = Math.floor((colIndex - 1) / 26);
    }
    return letter;
}

// 6) CALCULAR EL RANGO DE ESCRITURA EN BASE A startRow Y newValues.length
const firstCell = "A" + startRow;
const lastCol = columnLetter(newValues.length);
const lastCell = lastCol + startRow;
const range = `${sheetName}!${firstCell}:${lastCell}`;

// 7) EXTRAER EL access_token DE msg.payload (resultado del nodo anterior)
const tokenResponse = msg.payload;
const accessToken = tokenResponse.access_token;

// 8) PREPARAR ENCABEZADOS Y MÉTODO PARA EL HTTP REQUEST
msg.headers = {
    Authorization: `Bearer ${accessToken}`,
    "Content-Type": "application/json"
};                                                // Usamos PUT para actualizar rangos

// 9) MONTAR EL CUERPO DE LA PETICIÓN CON EL FORMATO DE LA API
msg.payload = {
    range: range,
    majorDimension: "ROWS",
    values: [newValues]
};

// 10) CONSTRUIR LA URL CON valueInputOption=RAW
msg.url =
    `https://sheets.googleapis.com/v4/spreadsheets/` +
    `${spreadsheetId}/values/${encodeURIComponent(range)}` +
    `?valueInputOption=RAW`;

// 11) RETORNAR msg PARA QUE EL NODO HTTP REQUEST LO PROCESO
return msg;

