let persistedEstado = flow.get(EST_KEY2); // puede ser undefined o { inptChe: '0'|'1' }

// Si el mensaje trae estadoCss con propiedad inptChe válida, guardarlo (solo entonces)
if (msg.hasOwnProperty('estadoCss2') && msg.estadoCss2 && msg.estadoCss2.hasOwnProperty('inptChe2')) {
  // Guardar exactamente lo que viene; normalizamos a '1' o '0' según el valor:
  const v = msg.estadoCss2.inptChe2 === '1' ? '1' : '0';
  flow.set(EST_KEY2, { inptChe2: v });
  persistedEstado = { inptChe2: v };
}

// ----------------- Lógica original (usando valores persistidos) -----------------

// 1) Si request1 === '0' y no hay estadoCss persistido => activar flag = 1 y no retornar nada
if ((persistedRequest === '0' && persistedEstado.inptChe2 === '0') || (persistedRequest === '1' && persistedEstado.inptChe2 === '0')) {
  flag = 1;
  flow.set(FLAG_KEY2, flag);
  msg.updatecss2 = persistedEstado.inptChe2;
  return msg;
}

// 2) Si la bandera está en 1 => moverla a 2 y no retornar nada
if (flag === 1) {
  flag = 2;
  flow.set(FLAG_KEY2, flag);
  return null;
}

// 3) Si request1 (persistido) es '1' => no retornamos
if (persistedRequest === '1') {
  return null;
} else {
  // 4) En cualquier otro caso devolvemos updatecss con el valor persistido en estadoCss.inptChe
  //return { updatecss: persistedEstado && persistedEstado.inptChe };
  msg.updatecss2 = persistedEstado.inptChe2;
  return msg; 
}
